pm.test("Response contains attacks with https protocol", function () {
    var jsonData = pm.response.json();
    pm.expect(jsonData.data.length).to.be.at.least(0);
});

pm.test("All attacks match all filter conditions", function () {
    var jsonData = pm.response.json();
    jsonData.data.forEach(attack => {
        pm.expect(attack.frequency).to.equal('high');
        pm.expect(attack.danger).to.equal('critical');
        pm.expect(attack.attack_type).to.equal('application');
    });
});

